import { Component, OnInit } from "@angular/core";
import { AuthService } from "../auth/auth.service";
import { Router } from "@angular/router";
import { Observable } from "rxjs";

@Component({
  selector: "app-dashboard",
  templateUrl: "./dashboard.component.html",
  styleUrls: ["./dashboard.component.css"],
})
export class DashboardComponent implements OnInit {
  currentUser$: Observable<any>;

  constructor(
    private authService: AuthService,
    private router: Router,
  ) {
    this.currentUser$ = this.authService.currentUser$;
  }

  ngOnInit(): void {}

  logout(): void {
    this.authService.logout();
    this.router.navigate(["/login"]);
  }
}
<div class="dashboard-container">
  <nav class="navbar">
    <div class="navbar-brand">
      <h2>Meu Dashboard</h2>
    </div>
    <div class="navbar-menu">
      <div *ngIf="currentUser$ | async as user" class="user-info">
        <span>Bem-vindo, {{ user.name }}</span>
        <button class="btn btn-logout" (click)="logout()">Sair</button>
      </div>
    </div>
  </nav>

  <div class="content">
    <h1>Dashboard</h1>
    <p>Você está autenticado e pode acessar esta página protegida.</p>

    <div *ngIf="currentUser$ | async as user" class="user-card">
      <h3>Informações do Usuário</h3>
      <p><strong>Nome:</strong> {{ user.name }}</p>
      <p><strong>Email:</strong> {{ user.email }}</p>
    </div>
  </div>
</div>
